#Define the password and the desired domainname$passwordstring = "password" #Enter the same password you used during the initial install of the server.$domainname = "corp.bjornhouben.com"#Install and configure Active Directory. Automatically reboots.Install-WindowsFeature AD-Domain-Services -IncludeManagementTools$Password = ConvertTo-SecureString $passwordstring -AsPlainText -ForceInstall-ADDSForest -DomainName $domainname -SafeModeAdministratorPassword $Password -Force